import {
	FETCH_GALLERY_START,
	FETCH_GALLERY_SUCCESS,
	FETCH_GALLERY_FAILURE,
	FILTER_CHANGED_SUCCESS
} from 'actionTypes';
import { fetchGallery as fetchGalleryApi } from 'api';

export const getGallery = (isPending) => async dispatch => {
	if(!isPending) {
		dispatch({
			type: FETCH_GALLERY_START
		});
		try{
			let galleryList = await fetchGalleryApi();
			dispatch({
				type: FETCH_GALLERY_SUCCESS,
				payload: galleryList
			})
		} catch (err) {
			dispatch({
				type: FETCH_GALLERY_FAILURE,
				payload: err,
				error: true
			})
		}
	}
};
export const setFilter = (filterValue) => dispatch => {
	dispatch({
		type: FILTER_CHANGED_SUCCESS,
		payload: filterValue
	})
};