import React, { Component } from 'react';

class Button extends Component {
	constructor(props) {
		super(props);
		this.autoRefreshHandler = this.props.autoRefreshHandler;
		this.state = {autoRefreshOn: false}
	}
	toggleAutoRefresh() {
		this.autoRefreshHandler(this.state.autoRefreshOn);
		this.setState((prevState, props) => ({
			autoRefreshOn: !prevState.autoRefreshOn
		}))
	}
	render() {
		return (
			<button onClick={this.toggleAutoRefresh.bind(this)}>{this.state.autoRefreshOn ? `Stop auto-refresh` : `Start auto-refresh`}</button>
		)
	}
}
export default Button