import React, { Component } from 'react';
import 'components/Filter/filter.css';
import { connect } from 'react-redux';

class Filter extends Component{
	constructor(props) {
		super(props);
		this.sliderElement = null;
		this.setFilter = this.props.setFilter;
	}
	changeFilter() {
		this.setFilter(this.sliderElement.value)
	}
	render() {
		return (
			<div className="slidecontainer">
				<input onChange={this.changeFilter.bind(this)} ref={(input) => {this.sliderElement = input}} type="range" min="1" max="100" className="slider" id="myRange" />
			</div>
		)
	}
}
export default Filter