import React, { Component } from 'react';
import 'components/Filter/filter.css';

class Filter extends Component{
	constructor(props) {
		super(props);
		this.sliderElement = null;
		this.setFilter = this.props.setFilter;
	}
	changeFilter() {
		this.setFilter(this.sliderElement.value)
	}
	render() {
		this.filterValue = this.props.filterValue;
		return (
			<div className="slidecontainer">
				<input onChange={this.changeFilter.bind(this)} ref={(input) => {this.sliderElement = input}} type="range" min="1" max="1000" defaultValue="5" className="slider" id="myRange" />
				<div className="filter-value">
					{this.filterValue}
				</div>
			</div>
		)
	}
}
export default Filter