import React from 'react';
import ItemList from 'components/ItemList';

const List = ({galleryData, filterValue}) => {
	console.log('galleryData', galleryData);
	return (
		<div className="list-wrapper">
			{galleryData.length > 0 ? galleryData.filter((filteredItem, index) => { return filteredItem.data.num_comments > filterValue}).map((item, index) =>
				<ItemList key={index} itemData={item.data} />
			) : `Loading...`}
		</div>
	)
};
export default List