import {
	FETCH_GALLERY_START,
	FETCH_GALLERY_SUCCESS
} from 'actionTypes';
const initialState = {};

export default (state = initialState, {type, payload}) => {
	switch(type) {
		case FETCH_GALLERY_START:
			return {isPending: true};
		case FETCH_GALLERY_SUCCESS:
			return Object.assign(payload, {isPending: false});
		default:
			return state
	}
}